      target_index = 0 
      target_index(1) = kinorder
      maxnterms = FLQindex(trove%Nmodes_e,target_index)
      Nmodes = PT%Nmodes
      !
      do kclass=1, PT%nclasses
        nclass_imode(1:2,kclass) = (/PT%mode_class(kclass,1),PT%mode_class(kclass,PT%mode_iclass(kclass))/)
      enddo
