                                            nterms_class,me_contr)

  integer(ik), intent(in) :: k1_,k2_,iclass,nmodes_class1,nmodes_class2,ncontr,nterms_class
  character(cl), intent(in) :: func_tag
  real(rk), intent(in) :: me_contr(:,:,:)

  integer(ik) :: IOunit, info
