            !
            if (treat_rotation) then
              !
              call grot_gcor_sum(dimen,idimenmax,cdimens(jcontr,:),icontr_stored(:,istore,:),grot_t,gcor_t1,gcor_t2,&
                                 vec_stored(:,istore,:),grot(:,:,ib),gcor(:,:,ib))
              !
              !do k = 1,cdimens(jcontr,0)
