                                     ijmode_icomb_coefs(:,0:ncomb))
          !
          if (verbose>=5) then 
             ! check if we are able to recover all expansion terms from the unique for each class terms
             write(my_fmt,'(a,i0,a)') "(/a,1x,",nmodes,"(1x,i3),1x,a)"
             do icoeff = 1,trove%g_rot(jmode,imode)%Ncoeff
